package main

import "fmt"

func floor(input string) int {
	var c int
	for _, r := range input {
		switch r {
		case '(':
			c++
		case ')':
			c--
		}
	}
	return c
}

func basement(input string) int {
	var c int
	for step, r := range input {
		switch r {
		case '(':
			c++
		case ')':
			c--
		}
		if c < 0 {
			return step + 1
		}
	}
	return -1
}

type testcase struct {
	in       string
	expected int
}

func main() {

	fmt.Println("")
	fmt.Println("Advent of Code - 2015 - Day - 1")

	tcs := []testcase{
		testcase{"(())", 0},
		testcase{"()()", 0},
		testcase{"(((", 3},
		testcase{"(()(()(", 3},
		testcase{"))(((((", 3},
		testcase{"())", -1},
		testcase{"))(", -1},
		testcase{")))", -3},
		testcase{")())())", -3},
	}

	if false {

		for _, tc := range tcs {
			fmt.Println(tc.in, "-->", tc.in, floor(tc.in) == tc.expected)
		}

		tcs = []testcase{
			testcase{")", 1},
			testcase{"()())", 5},
		}
		for _, tc := range tcs {
			fmt.Println(tc.in, "-->", tc.in, basement(tc.in) == tc.expected)
		}
	}

	fmt.Println("Part 1 is", floor(input))    // 9 minutes
	fmt.Println("Part 2 is", basement(input)) // 4 minutes

}

